    #Process Functions
    def SearchProduct(self):
        query = self.edProdSearch.text()
        if len(query) > 3:
            df = pd.DataFrame(self.products.GetProducts(query)).rename(columns= self.products.colouns_pt)
            self.tbProd.setModel(QModel(df))
    

    def updateProductInfo(self,prop,value):
        setattr(self.product,prop,value)
        
    def saveProduct(self):
        if self.product.save() :
            self.edProdName.clear()
            self.edProdDescription.clear()
            self.cbProdColor.setCurrentIndex(0)
            self.edProdSuplyer.clear()
            self.chbProdWifi.setChecked(False)
            self.chbProdZegbee.setChecked(False)
            self.chbProdRF.setChecked(False)
            self.cbProdUnitType.setCurrentIndex(0)
            self.edProdBarcode.clear()
            self.edProdValue.clear()
            self.lsProdAttr.clear()
            self.cbProdAttr.clear()
            self.cbProdAttr.addItem("Acionadores")
            self.cbProdAttr.addItem("Tomadas")
            self.cbProdAttr.addItem("Entradas")
            self.cbProdAttr.addItem("Saidas")
            
    def setProduct(self):
        self.product.getPoduct(self.edPushProduct.text())
        if self.product.unit_type == 'length':
            self.CbPushUni.addItems(('mm','cm','M','Km'))
        else:
            self.CbPushUni.addItems(('UN',))
        #self.EdPushQTD.setValue(self.product.)

    def PusheSave(self):
        if (self.purchaseList.save()):
            self.tbPushItens.setModel(QModel(pd.DataFrame([])))
            
    # UI functions
    def changeModule(self,index):
        self.stackedWidget.setCurrentIndex(index)

    def changeTitle(self):
        if self.stackedWidget.currentIndex() == 0:
            self.lbPgActual.setText('Estoque')
        elif self.stackedWidget.currentIndex() == 1:
            self.lbPgActual.setText('Compras')
        elif self.stackedWidget.currentIndex() == 2:
            self.lbPgActual.setText('Vendas')
        elif self.stackedWidget.currentIndex() == 3:
            self.lbPgActual.setText('Produtos')

    def listProdusts(self):
        query = self.edPushProduct.text()
        prods = self.products.GetProducts(query)
        self.prodsPushDict = { x['name']:x['pk'] for x in prods }
        self.model.setStringList(self.prodsPushDict.keys())


    def addAttribute(self):
        attr = self.cbProdAttr.currentText()
        value = self.edProdAttrValue.text()
        item4list = '{} : {}'.format(attr,value)
        self.lsProdAttr.addItem(item4list)
        self.product.addattrs(attr,value)
        self.cbProdAttr.removeItem(self.cbProdAttr.currentIndex())
        self.edProdAttrValue.clear()

    def addIntemPusher(self):
        self.purchaseList.addItem(self.product,
                                  self.EdPushQTD.value(),
                                  self.CbPushUni.currentText(),
                                  self.EdPushValue.value())
        df = pd.DataFrame(self.purchaseList.PurchaTable)#.rename(columns= self.products.colouns_pt)
        self.tbPushItens.setModel(QModel(df))
        self.product = ClientRest.Produto()
        self.edPushProduct.clear()
        self.EdPushQTD.clear()
        self.CbPushUni.clear()
        self.EdPushValue.clear()
        


